__NUXT_JSONP__("/blog/2021-11-29-article-3", (function(a,b,c){return {data:[{post:{slug:"2021-11-29-article-3",description:"The importance of UI\u002FUX design",title:"UI\u002FUX Design",date:"2021-11-29T21:21:09.405Z",banner:"https:\u002F\u002Fimages.unsplash.com\u002Fphoto-1476357471311-43c0db9fb2b4?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",toc:[],body:{type:"root",children:[{type:b,tag:c,props:{},children:[{type:a,value:"Here at Vexus, we take the fundamentals of UI\u002FUX design very seriously because the design of the website is crucial in keeping a potential client's interest long enough for them to engage with your company. We like to create innovative designs while keeping the interface simplistic yet intriguing enough for the user to want to keep scrolling and explore every part of the website."}]},{type:a,value:"\n"},{type:b,tag:c,props:{},children:[{type:a,value:"With our intuitive design, users can scan smoothly, achieve their goal in minimal clicks and leave the website feeling great with how convenient everything was processed."}]}]},dir:"\u002Fblog",path:"\u002Fblog\u002F2021-11-29-article-3",extension:".md",createdAt:"2021-11-29T21:22:10.832Z",updatedAt:"2021-12-05T23:24:15.008Z"},_img:{"/_ipx/h_600/assets/images/https://images.unsplash.com/photo-1476357471311-43c0db9fb2b4%3Fixlib=rb-1.2.1%26ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8%26auto=format%26fit=crop%26w=1170%26q=80":"\u002F_nuxt\u002Fimage\u002F033214[ext]"}}],fetch:{},mutations:void 0}}("text","element","p")));